export enum JettonDeployState {
    NOT_STARTED,
    BALANCE_CHECK,
    UPLOAD_IMAGE,
    UPLOAD_METADATA,
    AWAITING_MINTER_DEPLOY,
    AWAITING_JWALLET_DEPLOY,
    VERIFY_MINT,
    ALREADY_DEPLOYED,
    DONE
}

export interface JettonDeployParams {
