export const JETTON_WALLET_CODE = Cell.fromBoc(fs.readFileSync("build/jetton-wallet.cell"))[0];
export const JETTON_MINTER_CODE = Cell.fromBoc(fs.readFileSync("build/jetton-minter.cell"))[0]; // code cell from build output

enum OPS {
  Mint = 21,
  InternalTransfer = 0x178d4519,
  Transfer = 0xf8a7ea5,
}

